	// Variables
	String userSelect = "";  // Holds the string value of the users input
	int playerLvl = 0;  // Holds the value for the player characters level
	
	
	// Arrays for Enemy Generation
	// These arrays hold the data required for enemy generation
	// (Ass ton of Parallel arrays)
	String[] enemyNames = {};  // Holds the names of the enemy type
	int[] enemyHealth = {};  // Holds the health of the enemy
